export const productSlice = createSlice({
  name: "product",
  initialState,
  reducers: {
    getById: (state, action: PayloadAction<ProductId>) => {
      const productId = action.payload.id;
      const foundProduct = state.products.find(
        (product) => product.id === productId
      );
      if (foundProduct) {
        state.selectedProduct = foundProduct;
      }
    },
    addChart: (state, action: PayloadAction<Product>) => {
      if (action.payload) {
        const existingProductIndex = state.chart?.findIndex(
          (item) => item.product.id === action.payload.id
        );
        if (existingProductIndex !== -1) {
          state.chart[existingProductIndex].totalProduct += 1;
          state.total += action.payload.price;
          state.total += action.payload.price;
        } else {
          state.chart?.push({
            product: action.payload,
            amount: action.payload.price,
            totalProduct: 1,
          });
          state.total = action.payload.price;
        }
      }
    },
    addTotalProductInCart: (state, action: PayloadAction<Product>) => {
      if (action.payload) {
        const exixtingProductIndex = state.chart?.findIndex(
          (item) => item.product.id === action.payload.id
        );
        if (exixtingProductIndex !== -1) {
          state.chart[exixtingProductIndex].totalProduct += 1;
          state.total += action.payload.price;
        }
      }
    },
    minusTotalProductInCard: (state, action: PayloadAction<Product>) => {
      if (action.payload) {
        const existingProductIndex = state.chart?.findIndex(
          (item) => item.product.id === action.payload.id
        );
        if (existingProductIndex !== -1) {
          if (state.chart[existingProductIndex].totalProduct > 0) {
            state.chart[existingProductIndex].totalProduct -= 1;
            state.total -= action.payload.price;
          }
          if (state.chart[existingProductIndex].totalProduct === 0) {
            state.chart?.splice(existingProductIndex, 1);
            state.total = 0;
          }
        }
      }
    },
    removeProductInChart: (state, action: PayloadAction<Product>) => {
      const removeProduct = action.payload;
      const index = state.chart?.findIndex(
        (product) => product.product.id === removeProduct.id
      );

      if (index !== -1) {
        state.chart?.splice(index, 1);
        state.total = 0
      }
    },
  },
});

export const {
  getById,
  addChart,
  removeProductInChart,
  addTotalProductInCart,
  minusTotalProductInCard,
} = productSlice.actions;
export default productSlice.reducer;
